sysauto_script.script:
// Get Today's date and time
	var rightNow = new GlideDateTime();

//Query the database for NeedIt task records with Duedate field values older than
// the current time. Only return NeetIt task records that do not have 
// a State field value as Closed Complete.
var overdueNTTasks = new GlideRecord('x_58872_needit_needit_task');
overdueNTTasks.addQuery('due_date','<=',rightNow);
overdueNTTasks.addQuery('state','!=',3);

overdueNTTasks.query();
// write a log message for each overdue NeedIt task record
while (overdueNTTasks.next()){
	gs.info("Overdue NeedIT Task record number =" + overdueNTTasks.number);
        gs.eventQueue('x_58872_needit.overdueNeedItTask',overdueNTTasks, overdueNTTasks.number,gs.getUsername());
}
